  name: "Product Preview",
  render: () => <div className="p-8 flex justify-center">
      <HoverCard>
        <HoverCardTrigger asChild>
          <Button variant="outline">View Product</Button>
        </HoverCardTrigger>
        <HoverCardContent className="w-80">
          <div className="space-y-3">
            <img src="https://via.placeholder.com/320x180" alt="Product" className="w-full h-32 object-cover rounded-md" />
            <div className="space-y-2">
              <h4 className="text-sm font-semibold">Premium Headphones</h4>
              <p className="text-sm text-muted-foreground">
                High-quality wireless headphones with noise cancellation and 30-hour battery life.
              </p>
              <div className="flex items-center justify-between">
                <span className="text-lg font-semibold">$299.99</span>
                <span className="text-sm text-green-600">In Stock</span>
              </div>
            </div>
          </div>
        </HoverCardContent>
      </HoverCard>
    </div>
